#!/bin/bash

# Create a hardened Tor browser profile for intelligence operations
# This script should be run from a secure operating system like Tails or Whonix

# Define the profile directory
PROFILE_DIR="$HOME/TorBrowser/Data/Browser/profile.intelligence"

# Create the profile directory if it doesn't exist
mkdir -p "$PROFILE_DIR"

# Create a user.js file with hardened security preferences
cat > "$PROFILE_DIR/user.js" << EOL
// Disable JavaScript by default - enable selectively when needed
user_pref("javascript.enabled", false);

// Set security level to Safest
user_pref("security.tor_browser.level", 2);

// Prevent WebRTC leaks
user_pref("media.peerconnection.enabled", false);
user_pref("media.navigator.enabled", false);

// Disable geolocation
user_pref("geo.enabled", false);

// Disable browser cache
user_pref("browser.cache.disk.enable", false);
user_pref("browser.cache.memory.enable", false);

// Disable browser history
user_pref("places.history.enabled", false);

// Standardize fingerprint
user_pref("privacy.resistFingerprinting", true);
user_pref("privacy.fingerprintingProtection", true);

// Force HTTPS
user_pref("dom.security.https_only_mode", true);

// Disable WebGL
user_pref("webgl.disabled", true);

// Disable clipboard operations
user_pref("dom.event.clipboardevents.enabled", false);

// Disable camera and microphone
user_pref("media.navigator.video.enabled", false);
user_pref("media.navigator.audio.enabled", false);

// Disable notifications
user_pref("dom.webnotifications.enabled", false);

// Disable browser auto-updates for this profile
user_pref("app.update.auto", false);

// Disable search suggestions
user_pref("browser.search.suggest.enabled", false);

// Disable form autofill
user_pref("browser.formfill.enable", false);

// Disable password saving
user_pref("signon.rememberSignons", false);

// Disable telemetry
user_pref("toolkit.telemetry.enabled", false);
user_pref("toolkit.telemetry.unified", false);
user_pref("toolkit.telemetry.archive.enabled", false);

// Disable health reports
user_pref("datareporting.healthreport.uploadEnabled", false);
user_pref("datareporting.healthreport.service.enabled", false);

// Disable crash reports
user_pref("breakpad.reportURL", "");
user_pref("browser.tabs.crashReporting.sendReport", false);

// Disable prefetching
user_pref("network.prefetch-next", false);
user_pref("network.dns.disablePrefetch", true);

// Disable link prefetching
user_pref("network.http.speculative-parallel-limit", 0);

// Disable browser pings
user_pref("browser.send_pings", false);

// Disable offline cache
user_pref("browser.cache.offline.enable", false);

// Disable site-specific zoom
user_pref("browser.zoom.siteSpecific", false);

// Disable accessibility services
user_pref("accessibility.force_disabled", 1);

// Disable media device enumeration
user_pref("media.navigator.enabled", false);
user_pref("media.peerconnection.enabled", false);

// Disable WebRTC
user_pref("media.peerconnection.enabled", false);
user_pref("media.peerconnection.turn.disable", true);
user_pref("media.peerconnection.use_document_iceservers", false);
user_pref("media.peerconnection.video.enabled", false);
user_pref("media.peerconnection.identity.timeout", 1);

// Isolate to first party
user_pref("privacy.firstparty.isolate", true);

// Enable New Identity on browser restart
user_pref("privacy.sanitize.sanitizeOnShutdown", true);
EOL

# Implement network isolation settings
cat > "$PROFILE_DIR/torrc" << EOL
# Use a dedicated Tor circuit for this profile
SocksPort 9150 IsolateDestAddr IsolateDestPort

# Use bridges if needed (uncomment and add bridges as required)
#UseBridges 1
#Bridge obfs4 X.X.X.X:XXXX FINGERPRINT

# Avoid countries with extensive monitoring (customize as needed)
ExcludeNodes {cn},{ru},{ir},{sy},{??}

# Strict nodes for exit selection (customize as needed)
#StrictNodes 1
#ExitNodes {us},{ca},{gb},{de},{fr},{se},{nl}
EOL

# Add security add-ons configuration
mkdir -p "$PROFILE_DIR/extensions"

# Download and configure NoScript with maximum security settings
cat > "$PROFILE_DIR/extensions/noscript_config.json" << EOL
{
  "policy": {
    "DEFAULT": {
      "capabilities": []
    },
    "TRUSTED": {
      "capabilities": ["script", "object", "media", "frame", "font", "webgl", "fetch", "ping"]
    },
    "UNTRUSTED": {
      "capabilities": []
    }
  },
  "sites": {
    "trusted": [],
    "untrusted": []
  },
  "temp": {
    "enabled": false
  },
  "autoAllowTop": false
}
EOL

# Create a launcher script
cat > "$HOME/launch_intelligence_browser.sh" << EOL
#!/bin/bash

# Launch Tor Browser with the intelligence profile
# First, ensure Tor is running with the custom configuration
tor -f "$PROFILE_DIR/torrc" &
TOR_PID=\$!

# Wait for Tor to establish circuits
sleep 30

# Launch the browser with the hardened profile
"$HOME/tor-browser/Browser/start-tor-browser" --profile "$PROFILE_DIR" --allow-remote

# Clean up when browser closes
kill \$TOR_PID
EOL

chmod +x "$HOME/launch_intelligence_browser.sh"

echo "Hardened intelligence profile created at $PROFILE_DIR"
echo "Launch with: $HOME/launch_intelligence_browser.sh"
